package print;

public class Print01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "안형욱";
		String myAge = "36";
		
		System.out.println("* 이름 : "+myName);
		System.out.println("* 나이 : "+myAge);
		System.out.println();
		System.out.println("* 이름 : "+myName+"\t"+"* 나이 : "+myAge);
		
	}

}
